import React, { Component } from 'react';
import {Row,Col,Button,Grid } from 'react-bootstrap';
import Data from '../Data';
import Product from '../Component/Product';

class BestSeller extends Component {
    
  constructor(props) {
    super(props);
    this.state = {
        content: []
    }
  }
  buildContent() {
    let _content = [];

    let data = Data.data;
    for(var i in data) {
        let href = '/product/'+data[i].id;
        _content.push(
            <Col key={'product'+i} xs={4} md={3} xs={3}>
                <a  href={href}>
                    <Product data={data[i]}></Product>
                </a>
            </Col>
        );
    }
    let _productContent = [];
    _productContent.push(<Row key={10}>{_content}</Row>);
    return _productContent;
  }

  componentDidMount() {
    this.setState({
        content: this.buildContent()
    });
}
  render() {
    return (
        <div className="new-product">
            <Grid>
                <Row className="my-row">
                    <Col sm={3} lg={3} md={3} xs={3} >
                        <hr className="line-decoration"/>
                    </Col>
                    <Col sm={6} lg={6} md={6} xs={6} >
                        <h1 className="unit"><b>BEST SELLER</b></h1>
                    </Col>
                    <Col sm={3} lg={3} md={3} xs={3} >
                        <hr className="line-decoration"/>
                    </Col>
                </Row>
                <br/><br/>
                <Row className="my-row">
                        {this.state.content}
                </Row>
                <br/><br/>
                <Row className="my-row" >
                    <Col sm={5} lg={5} md={5} xs={5} >
                    </Col>
                    <Col sm={2} lg={2} md={2} xs={2} >
                        <Button className="btn-more">More</Button>
                    </Col>
                    <Col sm={5} lg={5} md={5} xs={5} >
                    </Col>
                </Row>
            </Grid>
        </div>
    );
  }
}

export default BestSeller;
