import React, { Component } from 'react';
import {Row,Col,Grid,Button } from 'react-bootstrap';
import Data from '../Data';
import Product from './Product';

class NewProduct extends Component {
    
  constructor(props) {
    super(props);
    this.state = {
        content: []
    }
  }
  buildContent() {
    let _content = [];

    let data = Data.data;
    for(var i in data) {
        _content.push(
            <Col key={i} xs={6} md={4}>
                <a className="btn" href="/product-id=">
                <Product data={data[i]}></Product>
                </a>
            </Col>
        );
    }
    let _productContent = [];
    _productContent.push(<Grid key="content"><Row key={i}>{_content}</Row></Grid>);
    return _productContent;
  }

  componentDidMount() {
    this.setState({
        content: this.buildContent()
    });
}
  render() {
    return (
        <div className="new-product">
            <Row className="my-row">
                <Col sm={2} lg={2} md={2} xs={2} lgPush={2} mdPush={2} smPush={2} xsPush={2}>
                    <hr className="line-decoration"/>
                </Col>
                <Col sm={8} lg={8} md={8} xs={8} >
                    <h1 className="unit"><b>NEW PRODUCT</b></h1>
                </Col>
                <Col sm={2} lg={2} md={2} xs={2} lgPull={2} mdPull={2} smPull={2} xsPull={2}>
                    <hr className="line-decoration"/>
                </Col>
            </Row>
            <br/><br/>
            <Row className="my-row">
                {this.state.content}
            </Row>
            <Row className="my-row" >
                <Col sm={5} lg={5} md={5} xs={5} lgPush={2} mdPush={2} smPush={2} xsPush={2}>
            
                </Col>
                <Col sm={2} lg={2} md={2} xs={2} >
                    <Button className="btn-more">More</Button>
                </Col>
                <Col sm={5} lg={5} md={5} xs={5} lgPull={2} mdPull={2} smPull={2} xsPull={2}>
                </Col>
            </Row>
        </div>
    );
  }
}

export default NewProduct;
