import React, { Component } from 'react';
import {Row,Col,Button } from 'react-bootstrap';
import Data from '../Data';
import Product from '../Component/Product';
import Api from '../Api';

class InfoRight extends Component {
    constructor(props) {
        super(props);
        this.state = {
            content: [],
            cateId: this.props.getInfo[0],
            brandId: this.props.getInfo[1],
        }
    }


    loadProduct() {
        let data = {
            id :'',
        }
        if(isNaN(this.props.getInfo[0])){
            data.id = parseInt(this.props.getInfo[1]);
        }else{
            data.id = parseInt(this.props.getInfo[0]);
        }

        Api.get("http://127.0.0.1:8000/getProductByCategory", data)
            .then((result) => {
                if(result.status){
                    this.setState({
                        content: result.product,
                    });
                }
            })
    }
    buildContent() {
        let _content = [];
        let data = this.state.content;
        let _productContent = [];
        console.log(this.state);
        // if(data.length>0){
        //     for(var i in data) {
        //         let href = '/product/'+data[i].id;
        //         _content.push(
        //             <Col key={i} lg={4} md={4} sm={6} xs={12}>
        //                 <a  href={href}>
        //                 <Product data={data[i]}></Product>
        //                 </a>
        //             </Col>
        //         );
        //     }
        //     _productContent.push(<Row key={i}>{_content}</Row>);
        // }
    
        return _productContent;
    }
    
    componentDidMount() {
       this.loadProduct();
    }


  render() {
    // let cate = Data.getCategoryById(parseInt(this.state.cateId));
    // let brand = Data.getBrandById(parseInt(this.state.brandId));
    // let temp ;
    // if(cate.length === 0){
    //     temp =brand[0];
    // }else{
    //     temp = cate[0];
    // }
    return (
        <div className="">
            <Row className="my-row">
                <Col sm={3} lg={3} md={2} xs={1} >
                    <hr className="line-decoration"/>
                </Col>
                <Col sm={6} lg={6} md={8} xs={10} >
                    {/* <h1 className="unit"><b>{temp.name}</b></h1> */}
                </Col>
                <Col sm={3} lg={3} md={2} xs={1} >
                    <hr className="line-decoration"/>
                </Col>
            </Row>
            <br/><br/>
           
            {this.buildContent()}
            <Row className="my-row" >
                <Col sm={5} lg={5} md={5} xs={5} >
            
                </Col>
                <Col sm={2} lg={2} md={2} xs={2} >
                    <Button className="btn-more">More</Button>
                </Col>
                <Col sm={5} lg={5} md={5} xs={5} >
                </Col>
            </Row>
        </div>
    );
  }
}

export default InfoRight;
