import React, { Component } from 'react';
import {Row,Col,Button } from 'react-bootstrap';
import Data from '../Data';
import Product from '../Component/Product';
class NavigationLeft extends Component {
    constructor(props) {
        super(props);
        this.state = {
            content: [],
            cateId: this.props.cateId,
        }
      }
      buildContent() {
        let _content = [];
    
        let data = Data.getProductByCate(1);
        for(var i in data) {
            _content.push(
                <Col key={i} sm={4} lg={4} md={4} xs={4}>
                    <a  href="/product-id=">
                    <Product data={data[i]}></Product>
                    </a>
                </Col>
            );
        }
        let _productContent = [];
            _productContent.push(<Row key={i}>{_content}</Row>);
        return _productContent;
      }
    
      componentDidMount() {
        this.setState({
            content: this.buildContent(),
            cateId: this.props.cateId.cateId,
        });
    }


  render() {
      console.log(this.state);
    return (
        <div className="new-product">
            <Row className="my-row">
                <Col sm={3} lg={3} md={3} xs={3} >
                    <hr className="line-decoration"/>
                </Col>
                <Col sm={6} lg={6} md={6} xs={6} >
                    <h1 className="unit"><b></b></h1>
                </Col>
                <Col sm={3} lg={3} md={3} xs={3} >
                    <hr className="line-decoration"/>
                </Col>
            </Row>
            <br/><br/>
           
            {this.state.content}
            <Row className="my-row" >
                <Col sm={5} lg={5} md={5} xs={5} lgPush={2} mdPush={2} smPush={2} xsPush={2}>
            
                </Col>
                <Col sm={2} lg={2} md={2} xs={2} >
                    <Button className="btn-more">More</Button>
                </Col>
                <Col sm={5} lg={5} md={5} xs={5} lgPull={2} mdPull={2} smPull={2} xsPull={2}>
                </Col>
            </Row>
        </div>
    );
  }
}

export default NavigationLeft;
