import React from 'react';
import '../App.css';
import Data from '../Data';
import {Row,Col,Table} from 'react-bootstrap';
import '../css/loginPage.css';

class ContentCartPage extends React.Component {

    constructor(props){
        super(props);
        this.state={
          content:[],
        }
    }

    buildContent(){
        let _content = [];
        let listProduct = Data.data;
        let yourCart = [];
        let yourCartJsonString = sessionStorage.getItem("cart");

        if (yourCartJsonString == null) {
            sessionStorage.setItem("cart", []);
        } else {
            if (yourCartJsonString !== '') {
                yourCart = JSON.parse(yourCartJsonString);
            }
            for (let i = 0; i < yourCart.length; i++) {

                let product = listProduct.find(product => product.id === yourCart[i].id);


                let _cart = '<td>'+i+'</td>';
                _cart+=  '<td>'+yourCart[i].product_name+'</td>';
                _cart+=  '<td>'+yourCart[i].price+'</td>';
                _cart+=  '<td>'+yourCart[i].quantity+'</td>';
                _cart+=  '<td>'+parseFloat(yourCart[i].price)*parseFloat(yourCart[i].quantity)+'</td>';
                _content.push(<tr key={i}>{_cart}</tr>);
            }
        }

        return _content;
    }

    componentDidMount(){
        this.setState({
            content: this.buildContent(),
        });
    }

    render() {
      return (

          <div>
               <Row className="contentPage my-row is-flex">
                    <Col lg={3} md={2} sm={1} xs={1}></Col>
                    <Col lg={6} md={8} sm={10} xs={12}>
                        <h1 className="title-page"><b>YOUR CART</b></h1>
                    </Col>
                    <Col lg={3} md={2} sm={1} xs={1}></Col>
                </Row>

                <Table responsive id="cart" className="table table-hover table-condensed">
                    <thead>
                        <tr>
                            <th>No.</th>
                            <th>Product</th>
                            <th>Price</th>
                            <th>Quantity</th>
                            <th className="text-center">Subtotal</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody id="your-cart">
                        <tr>
                            <td>1</td>
                            <td>1</td>
                            <td>1</td>
                            <td>1</td>
                            <td>1</td>
                        </tr>
                        {this.state.content}

                    </tbody>
                    <tfoot>
                    <tr>
                        <td><a href="/home" className="btn btn-warning"><i className="fa fa-angle-left"></i>
                                Continue Shopping</a></td>
                        <td colspan="3" className="hidden-xs"></td>
                        <td className="hidden-xs text-center">Total: $ <strong  id="total"></strong></td>
                        <td><a href="/payment" className="btn btn-success btn-block">Go to Payment<i
                                        className="fa fa-angle-right"></i></a></td>
                    </tr>
                    </tfoot>
                </Table>

          </div>
      );
    }
}

export default ContentCartPage;
