import React, { Component } from 'react';
import '../css/navigationPathCategory.css'
import Data from '../Data';

class NavigationLeft extends Component {

    constructor(props) {
        super(props);
        this.state = {
            content: []
        }
      }
      buildContent() {
        let _content = [];
        let cate = Data.cate;
        for(var i in cate) { 
          let href = '/productCateId/' +cate[i].id;
          _content.push(
               
               <li key={'3.'+ i}><a href={href}>{cate[i].name}</a></li>
          );
        }
        let _cateContent = [];
        _cateContent.push(<ul className="list-category" key={3}>{_content}</ul>);
        return _cateContent;
      }

      loadProductMore(numberSkip) {
        let data = {
            id: '',
            numberSkip:numberSkip,
            whatPage: 'Product',
        }
        if(isNaN(this.props.getInfo[0])){
            data.id = parseInt(this.props.getInfo[1]);
        }else{
            data.id = parseInt(this.props.getInfo[0]);
        }

        Api.get("http://127.0.0.1:8000/getProductByCategoryMore", data)
            .then((result) => {
                if(result.status){
                    let data = this.state.content.concat(result.products) ;
                    this.setState({
                        content:  data,
                        numberSkip: result.numberSkip,
                    });
                }
            })
    }

      buildContentBrand() {
        let _content = [];
        let brand = Data.brand;
        for(var i in brand) { 
          let href = '/productBrandId/' +brand[i].id;
          _content.push(
               
               <li key={'4.'+ i}><a href={href}>{brand[i].name}</a></li>
          );
        }
        let _brandContent = [];
        _brandContent.push(<ul className="list-category" key={4}>{_content}</ul>);
        return _brandContent;
      }
    
      componentDidMount() {
        this.setState({
            content: this.buildContent(),
            content_brand: this.buildContentBrand(),
        });
      } 

  render() {
    return (
        <div className="navigation-product">
            <div className=" category">
                <h1><b>Product</b></h1>
               {this.state.content}
            </div>
            <div className=" category">
                <h1><b>Brands</b></h1>
                {this.state.content_brand}
            </div>
            <div className=" category">
                <h1 ><b>Level</b></h1>
                <ul>
                    <li>Entry level</li>
                    <li>Semi pro</li>
                    <li>Pro level</li>
                </ul>
            </div>
        </div>
    );
  }
}

export default NavigationLeft;
