        // this.loadInfoUser();
    }


    render() {
        return (
            <div>
                <Row className="row is-flex account-info-general">
                    <Col lg={12}>
                    <Table id="cart" className="table table-hover table-condensed">
                        <thead>
                        <tr>
                            <th>Order At.</th>
                            <th>No.</th>
                            <th>Name</th>
                            <th>Price</th>
                            <th>Quantity</th>
                            <th className="text-center">Subtotal</th>
                            <th >Ajust</th>
                        </tr>
                        </thead>
                        <tbody id="my-order">
                        <tr>
                            <td colSpan="7">
                                You're not use to order
                                <a href="/home" className="btn btn-warning"><i className="fa fa-angle-left"></i>
                                    Go Shopping Now<span className="glyphicon glyphicon-shopping-cart"></span></a>
                            </td>
                        </tr>
                        </tbody>
                        <tfoot>
                        </tfoot>
                    </Table>
                    </Col>
                </Row>
            </div>
        );
    }
}

export default InfoOrder;
