import React from 'react';
import '../App.css';
import Data from '../Data';
import {Row,Col,Table} from 'react-bootstrap';
import '../css/yourCartPage.css';
import ProductRow from '../Component/ProductRow';

class ContentCartPage extends React.Component {

    constructor(props){
        super(props);
        this.state={
          content:[],
        }
    }

    buildContent(){
        let _content = [];
        let listProduct = Data.data;
        let yourCart = [];
        let yourCartJsonString = sessionStorage.getItem("cart");
        let total = 0;

        if (yourCartJsonString == null) {
            sessionStorage.setItem("cart", []);
        } else {
            if (yourCartJsonString !== '') {
                yourCart = JSON.parse(yourCartJsonString);
            }
            for (let i = 0; i < yourCart.length; i++) {

                let product = listProduct.find(product => product.id === yourCart[i].id);

                let data = {
                    'num': i,
                    'name': product.product_name,
                    'image': product.image,
                    'price' : product.product_price,
                    'quantity' : yourCart[i].quantity,
                    'subtotal' : (product.product_price*yourCart[i].quantity).toFixed(2),
                }
                total += product.product_price*yourCart[i].quantity;
                _content.push(<ProductRow data={data} key={i}></ProductRow>);
            }
            total = parseFloat(total).toFixed(2);
        }

        let _tableCart = [];
        _tableCart.push(<Table key={'table'} className="table table-hover table-condensed">
                    <thead>
                        <tr>
                            <th>No.</th>
                            <th>Product</th>
                            <th>Name</th>
                            <th>Quantity</th>
                            <th>Price</th>
                            <th className="text-center">Subtotal</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody id="your-cart">
                        {_content}
                    </tbody>
                    <tfoot>
                        <tr>
                            <td><a href="/home" className="btn btn-warning"><i className="fa fa-angle-left"></i>
                                    Continue Shopping</a></td>
                            <td colSpan="3" className="hidden-xs"></td>
                            <td className="hidden-xs text-center">Total: $ {total}<strong  id="total"></strong></td>
                            <td><a href="/payment" className="btn btn-success btn-block">Go to Payment<i
                                            className="fa fa-angle-right"></i></a></td>
                        </tr>
                    </tfoot>
       </Table>)
        return _tableCart;
    }

    componentDidMount(){
        this.setState({
            content: this.buildContent(),
        });
    }

    render() {
        console.log(this.state.content);
      return (
          <div>
               <Row className="contentPage my-row is-flex">
                    <Col lg={3} md={2} sm={1} xs={1}></Col>
                    <Col lg={6} md={8} sm={10} xs={12}>
                        <h1 className="title-page"><b>YOUR CART</b></h1>
                    </Col>
                    <Col lg={3} md={2} sm={1} xs={1}></Col>
                </Row>
                {this.state.content}
          </div>
      );
    }
}

export default ContentCartPage;
