import React, { Component } from 'react';
import './App.css';
import Api from './Api';


class MyData extends Component {

  
  constructor(props) {
    super(props);
    this.state = {
        content: this.buildContent(),
        cateList: this.loadCategoryList(),
    }
  }
  loadCategoryList() {
    Api.get("http://127.0.0.1:8000/getAllCategory", {limit: 10, start: 0})
        .then((rs) => {
          this.setState({cateList: rs.categories});
          console.log(rs.categories);
          return rs.categories;
        });
  }

  buildContent(){
    let _content = this.state.cateList;
    console.log(this.state.cateList);
    return _content;
  }
  componentDidMount() {
    this.setState = {
      content: this.buildContent(),
      cateList: this.loadCategoryList(),
  }

  }
  render() {
    return (
      <div>
        {this.state.content}
      </div>
    );
  }
}


export default MyData;
