import React, { Component } from 'react';
import {Row,Col,Table} from 'react-bootstrap';
import Api from '../Api';
import '../css/contactPage.css';
import ProductRowOrder from '../TableComponent/ProductRowOrder';

class InfoOrder extends Component {
    constructor(props) {
        super(props);
        this.state = {
            content: [],
            contentBill:[],
        }
        this.loadOrder = this.loadOrder.bind(this);
    }

    loadInfoUser(){
        let checkLogin = localStorage.getItem("tokenCustomer");
       
        if(checkLogin!= null){
          let data = {
            token: checkLogin
          }
            Api.get("http://127.0.0.1:8000/checkLogin", data)
            .then((result) => {
                console.log(result)
                if(result.status){
                    let htmlInfo = '';
                    let htmlShipping = '';
                    let name = result['customer']['name'];
                    let address = result['customer']['address'];
                    let city = result['customer']['city'];
                    let phone = result['customer']['phone'];
                    let email = result['customer']['email'];
                    let country = result['customer']['country'];
                    let check = false;
    
                    if (name == null) {
                        name = '';
                        check = true;
                    }
                    if (city == null) {
                        name = '';
                        check = true;
                    }
                    if (address == null) {
                        address = '';
                        check = true;
                    }
                    if (phone == null) {
                        phone = '';
                        check = true;
                    }
    
                    htmlInfo += '<li>Name: ' + name + '</li>\n' +
                        '<li>Address: ' + address + '</li>\n' +
                        '<li>Email: ' + email + '</li>\n' +
                        '<li>Phone: ' + phone + '</li>\n' +
                        '<li class="error-message" style="margin-top:10px;display: none"></li>\n';
                    window.jQuery('#account-info').append(htmlInfo);
    
                    htmlShipping += '<li>Default Shipping Address: ' + address + '</li>\n' +
                        '<li>Email: ' + email + '</li>\n' +
                        '<li>Phone: ' + phone + '</li>\n' +
                        '<li class="error-message-shipping" style="margin-top:10px;display: none"></li>\n';
    
                    window.jQuery('#account-shipping').append(htmlShipping);
                    if(check){
                        window.jQuery('.error-message').addClass('alert');
                        window.jQuery('.error-message').addClass('alert-warning');
                        window.jQuery('.error-message').html('You should fill information full to use all our service');
                        window.jQuery('.error-message').show('slow');
                    }
                    
                }
            })
        }else{
            window.location.assign("/");
        }
    }
     
    componentDidMount(){
        this.loadInfoUser();
    }


    render() {
        return (
            <div>
                <Row className="row is-flex account-info-general">
                    <Col lg={12}>
                    <Table id="cart" className="table table-hover table-condensed">
                        <thead>
                        <tr>
                            <th>Order At.</th>
                            <th>No.</th>
                            <th>Name</th>
                            <th>Price</th>
                            <th>Quantity</th>
                            <th className="text-center">Subtotal</th>
                            <th >Ajust</th>
                        </tr>
                        </thead>
                        <tbody id="my-order">
                            {this.state.contentBill}
                       
                        <tr>
                            <td colSpan="7">
                                You're not use to order
                                <a href="/" className="btn btn-warning"><i className="fa fa-angle-left"></i>
                                    Go Shopping Now<span className="glyphicon glyphicon-shopping-cart"></span></a>
                            </td>
                        </tr>
                        </tbody>
                        <tfoot>
                        </tfoot>
                    </Table>
                    </Col>
                </Row>
            </div>
        );
    }
}

export default InfoOrder;
