import React, { Component } from 'react';
import './App.css';
import Api from './Api';
import {Row} from 'react-bootstrap';

class MyData extends Component {

  
  constructor(props) {
    super(props);
    this.state = {
        content:[],
        cateList: this.loadCategoryList(),
        k:0,
    }
    this.loadCategoryList = this.loadCategoryList.bind(this);
  }
  loadCategoryList() {
    Api.get("http://127.0.0.1:8000/getAllCategory", {limit: 10, start: 0})
        .then((rs) => {
          this.setState({
            cateList: rs.categories,
            k: 1,
          });
        })
        .then((rs)=>{
          this.setState({
            content: this.buildContent(),
            k: 2,
          });
        });
        
  }

  buildContent(){
    let _content = [];
    console.log(this.state.cateList);
    for(let i in this.state.cateList){
      _content.push(<Row key={i}>{this.state.cateList[0].name}</Row>)
    }
    console.log(_content);
    return _content;
  }
 
  // componentDidMount(){
  //   this.setState({
  //     content: this.buildContent(),
  //   })
  // }

  
  render() { 
    console.log(this.state.cateList);
    return (
      <div>
        {this.state.content}
      </div>
    );
  }
}


export default MyData;
