import React, { Component } from 'react';
import './App.css';
import {Navbar,FormGroup,Button,FormControl,Nav,NavDropdown,NavItem,MenuItem} from 'react-bootstrap';
import Data from './Data';
import Api from './Api';
class Header extends Component {

  constructor(props) {
    super(props);
    this.state = {
        content: this.buildContent(),
        cateList: this.loadCategoryList(),
    }
     
  }

  buildContent() {
    let _content = [];
    let cate = Data.cate;
    for(var i in cate) { 
      let href = '/productCateId/' +cate[i].id;
      _content.push(
           <MenuItem key={'2.'+i} href={href}>{cate[i].name}</MenuItem>
      );
    }
    let _cateContent = [];
    _cateContent.push(<NavDropdown key={2} title="Products" id="mydrop">{_content}<MenuItem divider /><MenuItem eventKey={2.5} href="/contact">Contact</MenuItem></NavDropdown>);
    return _cateContent;
  }

 
  loadCategoryList() {
    Api.get("http://127.0.0.1:8000/getAllCategory", {limit: 10, start: 0})
        .then((rs) => {
          this.setState({cateList: rs.Data});
          console.log(rs.categories);
        });
  }


  componentDidMount() {


    window.jQuery('#search').autocomplete({
      source: function (request, response) {
        let data= {
          term: request.term
      }
      Api.get("http://127.0.0.1:8000/searchAutoComplete", data)
        .then((rs) => {
          response(window.jQuery.map(data, function (value, key) {
            return {
                label: value.name,
                value: value.name,
                id: value.id,
                image: value.image,
            }
          }));
        });
      },
      minLength: 1,
      autoFocus: true,
      select: function (ui) {
        window.jQuery('#search-id').val(ui.item.id);
        window.jQuery('.search-form').click(function(){
        window.location.assign('/product/'+ window.jQuery('#search-id').val());
        });
      },
  }).data("ui-autocomplete")._renderItem = function(ul, item) {
    let html = '';
    let url = '/product/'+ item.id;
    html += '<table class="directProductDetail"><input type="hidden" value='+url+'><tr><td width="30%"><img style="width:100px"src='+item.image+'></td><td style="color:black">'+item.value+'</td><td></td></tr></table>';
    window.jQuery('table').click(function(){
      window.jQuery('#search-id').val(item.id);
     
      let url = window.jQuery(this).children('input').val();
      window.location.assign(url);
    });
    return  window.jQuery("<li style='list-style-type: none;width:10%'>").data("ui-autocomplete-item", item).append(html).appendTo(ul);
};

   

  
  } 


  render() {
    return (
      
      <div>
          <meta name="csrf-token" content="{{ csrf_token() }}"></meta>
        <Navbar>
          <Navbar.Header>
            <Navbar.Brand>
              <a  href="/">Guitar 4U</a>
            </Navbar.Brand>
            <Navbar.Toggle />
          </Navbar.Header>
          <Navbar.Collapse>
            <Nav>
              <NavItem eventKey={1} href="/">
                Home
              </NavItem>
              {this.state.content}
            </Nav>
            
           
            <Nav pullRight>
              <NavItem href="/login">
                Login
              </NavItem>
              <NavItem href="/signUpPage">
                Sign Up
              </NavItem>
            </Nav>
            <Navbar.Form pullRight >
                <FormGroup >
                  <FormControl type="text" id="search" placeholder="Search" />
                  <FormControl type="hidden" id="search-id" name="id"/>
                </FormGroup>
                <Button type="button" className="search-form" >Search</Button>
              </Navbar.Form>
          </Navbar.Collapse>
        </Navbar>
      </div>
    );
  }
}

export default Header;
