import React, { Component } from 'react';
import {Row,Col,Button,Grid,Image } from 'react-bootstrap';
import Data from '../Data';
import Product from '../Component/Product';

class MoreProductByCategory extends Component {
    
    constructor(props) {
        super(props);
        this.state = {
            quantityFirst:0,
            content: this.buildContent(0),
        }
        this.getProductByQuantity = this.getProductByQuantity.bind(this);
    }
    getProductByQuantity(e){
        e.preventDefault();
        this.setState({
            quantityFirst: this.state.quantityFirst+4,
            content: this.buildContentMore(this.state.quantityFirst+4),
        })
    }

    buildContent(quantityStart) {
        let _content = []; 
        let product = Data.getProductById(this.props.productId);
        let data = Data.getProductByCategoryAndQuantity(product.cateId,quantityStart,quantityStart+4);
        let _productContent = [];
        for(let i in data) {
            let href = '/product/'+data[i].id;
            _content.push(
                <Col key={'product'+i} lg={3} md={4} sm={6} xs={12}>
                    <a  href={href}>
                        <Product data={data[i]}></Product>
                    </a>
                </Col>
            );
        }
        _productContent.push(<Row key={10}>{_content}</Row>);
        _productContent.push(<input key={11} type="text" valuedefault={quantityStart}/>);
    
        return _productContent;
       
    }

    buildContentMore(quantityStart) {
        let _content = [];
        let product = Data.getProductById(this.props.productId);
        let data = Data.getProductByCategoryAndQuantity(product.cateId,quantityStart,quantityStart+4);
        let _productContent = this.state.content;
        // _productContent.push(this.state.content);
        if(data.length === 0){
           
            _content.push(<Col key={'ranOutOf'}><p>Ran Out Of Product</p></Col>)
        
            _productContent.push(<Image key={'image!0'} className="loading" src="/assets/images/loading.gif"></Image>);
            _productContent.push(<Row key={'ran-out-of!0'} className="new adjust">{_content}</Row>);
        }else{
            for(let i in data) {
                let href = '/product/'+data[i].id;
                _content.push(
                    <Col key={'product'+i} lg={3} md={4} sm={6} xs={12}>
                        <a  href={href}>
                            <Product data={data[i]}></Product>
                        </a>
                    </Col>
                );
            }
            console.log(data);
            _productContent.push(<Image key={'image'+this.state.quantityFirst} className="loading" src="/assets/images/loading.gif"></Image>);
            _productContent.push(<Row key={'new-row'+this.state.quantityFirst} className="new adjust">{_content}</Row>);
            
        }
        return _productContent;
    }

  componentDidMount() {
    /*this.setState({
        content: this.buildContent(this.state.quantityFirst)
    });*/
    window.jQuery(".btn-more").click(function(){
        setTimeout(function(){
            window.jQuery(".new").hide();
        },50);
        setTimeout(function(){
            window.jQuery(".loading").fadeOut();
            setTimeout(function(){
                window.jQuery(".new").fadeIn();
            },200)
        },1000);
        
        if(window.jQuery(".adjust").hasClass("new")){
           window.jQuery("div").removeClass("new");
        }
    })  

    window.jQuery('#search').autocomplete({
        source: function (request, response) {
            window.jQuery.ajax({
                type: "GET",
                url: "/searchAutoComplete",
                dataType: "json",
                contentType: "application/json; charset=utf-8",
                data: {
                    term: request.term
                },
                cache: false,
                // data:'{QueryFilter: "' + request.term  + '"}',
                success: function (data) {
                    // console.log(request.term);
                    // console.log(data);
                    //
                    // //
                    response(window.jQuery.map(data, function (value, key) {
                        return {
                            label: value.name,
                            value: value.name,
                            id: value.id,
                            image: value.image,
                        }
                    }));


                }
            });
        },
        minLength: 1,
        autoFocus: true,
        select: function (e, ui) {
            window.jQuery('#search-id').val(ui.item.id);
        },
    }).data("ui-autocomplete")._renderItem = function(ul, item) {

        let html = '';
        html+= '                        <div class="row">\n' +
            '                            <div class="col-lg-6 col-sm-6 col-xs-6 col-md-6 item-search" ><div ">'+item.label+'</div></div>\n' +
            '                            <div class="col-lg-6 col-sm-6 col-xs-6 col-md-6 item-search" ><img alt="product"style="width: 30px;" src="uploads/'+item.image+'"></td>\n' +
            '                        </div>';
        return  window.jQuery("<li style='list-style-type: none'>").data("ui-autocomplete-item", item).append(html).appendTo(ul);
    };
}
  render() {
    return (
        <div className="">
             <Grid>
                <Row className="my-row">
                    <Col sm={3} lg={3} md={3} xs={3} >
                        <hr className="line-decoration"/>
                    </Col>
                    <Col sm={6} lg={6} md={6} xs={6} >
                        <h1 className="unit"><b>MORE PRODUCT</b></h1>
                    </Col>
                    <Col sm={3} lg={3} md={3} xs={3} >
                        <hr className="line-decoration"/>
                    </Col>
                </Row>
                <br/><br/>
                <Row className="my-row">
                        {this.state.content}
                </Row>
                <br/><br/>
                <Row className="my-row" >
                    <Col sm={5} lg={5} md={5} xs={5} >
                    </Col>
                    <Col sm={2} lg={2} md={2} xs={2} >
                        <Button className="btn-more" onClick={this.getProductByQuantity} >More</Button>
                    </Col>
                    <Col sm={5} lg={5} md={5} xs={5} >
                    </Col>
                </Row>
            </Grid>
        </div>
    );
  }
}

export default MoreProductByCategory;
