import React, { Component } from 'react';
import Page from '../Page';
import MyCarousel from '../HomePageComponent/Carousel';
import ContentProductPage from '../ProductPageComponent/ContentProductPage';
import {Row,Col,Button } from 'react-bootstrap';


class ProductPage extends Component {
    constructor(props){
        super(props);
        this.state={
          content:[],
          cateId:this.props.match.params.cateId,
        }
    }

    buildContent(){
        let cateInfo = [];
        cateInfo.push(this.state.cateId);

        let _content = [];
        _content.push(<MyCarousel key={1}></MyCarousel>);
        _content.push(<ContentProductPage key={2} cateId={cateInfo}></ContentProductPage>);
        
        return _content;
    }

    componentDidMount(){
        this.setState({
            content: this.buildContent(),
            cateId:this.props.match.params.cateId,
        });
    }

    render() {
        return (
            
            <div>
                <Row className="my-row is-flex">
                    <Col lg={3} md={2} sm={1} xs={1}></Col>
                    <Col lg={6} md={8} sm={10} xs={12}>
                        <h1 className="title-page"><b>ONE LOGIN TO ALL SERVICE</b></h1>
                    </Col>
                    <Col lg={3} md={2} sm={1} xs={1}></Col>
                </Row>
        <Row className="row form-login-input">
            <Col className="col-lg-4"></Col>
            <Col className="col-lg-4 login-input">
                <Row className="row">
                    {/* <Form id="login-form" method="post"> */}
                        {/* <div class="form-group">
                            <input type="text" class="form-control" id="email" placeholder="Enter email"
                                   name="email" value="">
                        </div>
                        <div class="form-group">
                            <input type="password" class="form-control" id="password" placeholder="Enter password"
                                   name="password" value="">
                        </div>
                        <div class="form-group">
                            <input class="check-box" type="checkbox" name="remember"> Remember me
                            <span class="forget-pass"><a href="#">Forgot Password ?</a></span>
                        </div>
                        <div class="form-group">
                            <div class="alert alert-warning message-error" style="display: none;color:red">
                            </div>
                            <button type="button" class="btn btn-default btn-submit">Sign In
                            </button>
                            <Button type="button" class="btn btn-default btn-create-new-account"><a href="/userSignUp">Create
                                    New Account</a></Button>
                            <input type="text" value="0" class="wrong-sign-in" hidden>
                        </div> */}
                    {/* </Form> */}
                </Row>
                <Row class="row btn-login-with-social-media">
                    <h4 className="h4-title-login-with-social-media">Login via social media</h4>
                    <Col className="col-lg-6 col-md-6 col-sm-6 col-xs-6" >
                        <Button type="button" class="btn btn-default btn-google">Google</Button>
                    </Col>
                    <Col className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                        <Button type="button" class="btn btn-default btn-facebook">Facebook</Button>
                    </Col>

                </Row>
            </Col>
            <Col className="col-lg-4"></Col>
        </Row>

             <Page content={this.state.content}></Page>
            </div>
        );
    }
}

export default ProductPage;
