import React from 'react';
import logo from'./logo.svg';


class Header extends React.Component { 
  constructor (props) {
    super(props)
    // state object
    this.state = { name : this.props.name,id : this.props.id,num : this.props.number}    
  }
  countUp (number) {
    // cập nhật tham số của state object → render method được gọi và vẽ lại
    this.setState({ num : number + 1 })
  }
  render() {
  	if(this.state.name =='lalala'){
  		return (


    	<div>
    	Name<input type="text" value="{this.state.name}"/>
    	Number<input type="text" value="{this.state.num}"/>
    	<button type="button" value="{this.state.num}"/>
    	<img src="https://img1.kakaku.k-img.com/images/article/pr/18/11_ATH-CKR7TW/img01.jpg"/>
    	</div>
    	);
  	}
    return (


    	<div>

    	<img src="https://img1.kakaku.k-img.com/images/article/pr/18/11_ATH-CKR7TW/img02.jpg"/></div>
    );
  }
}

export default Header;
