import React from 'react';
import '../App.css';
import {Row,Col,Button,Grid,FormGroup,Form,FormControl,Image} from 'react-bootstrap';
import '../css/paymentPage.css';
class ContentPaymentPage extends React.Component {


    constructor(props){
        super(props);
        this.state={
          content:[],
        }
    }

    buildContent(){
        let _content = [];
        let listProduct = Data.data;
        let yourCart = [];
        let yourCartJsonString = sessionStorage.getItem("cart");
        let total = 0;

        if (yourCartJsonString == null) {
            sessionStorage.setItem("cart", []);
        } else {
            if (yourCartJsonString !== '') {
                yourCart = JSON.parse(yourCartJsonString);
            }
            for (let i = 0; i < yourCart.length; i++) {

                let product = listProduct.find(product => product.id === yourCart[i].id);

                let data = {
                    'num': i,
                    'name': product.product_name,
                    'image': product.image,
                    'price' : product.product_price,
                    'quantity' : yourCart[i].quantity,
                    'subtotal' : (product.product_price*yourCart[i].quantity).toFixed(2),
                }
                total += product.product_price*yourCart[i].quantity;
                _content.push(<ProductRow data={data} key={i}></ProductRow>);
            }
            total = parseFloat(total).toFixed(2);
        }

        let _tableCart = [];
        _tableCart.push(<Table  id="cart"  key={'table'} className="table table-hover ">
                    <thead>
                        <tr>
                            <th>No.</th>
                            <th>Product</th>
                            <th>Name</th>
                            <th>Quantity</th>
                            <th>Price</th>
                            <th className="text-center">Subtotal</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody id="your-cart">
                        {_content}
                    </tbody>
                    <tfoot>
                        <tr>
                            <td><a href="/" className="btn btn-warning"><i className="fa fa-angle-left"></i>
                                    Continue Shopping</a></td>
                            <td colSpan="3" className="hidden-xs"></td>
                            <td className="hidden-xs text-center">Total: $ {total}<strong  id="total"></strong></td>
                            <td><a href="/payment" className="btn btn-success btn-block">Go to Payment<i
                                            className="fa fa-angle-right"></i></a></td>
                        </tr>
                    </tfoot>
       </Table>)
        return _tableCart;
    }

    componentDidMount(){
        this.setState({
            content: this.buildContent(),
        });
    }
    render() {
        return (
            <div>
                <Grid>
                    <Row className="my-row">
                        <Col lg={3} md={2} sm={1} xs={1}></Col>
                        <Col lg={6} md={8} sm={10} xs={10}>
                            <h1 className="title-page"><b>PAYMENT METHOD</b></h1>
                        </Col>
                        <Col lg={3} md={2} sm={1} xs={1}></Col>
                    </Row>
                    <Row className="my-row is-flex ">
                        <Col lg={6} md={6} sm={6} xs={12} className="payment-content">
                            <Form className="info-user" onsubmit="return validateInput()" method="GET">
                                <FormGroup className="form-group name-info-user">
                                    <Row>
                                        <Col lg={6}>
                                            <FormControl type="text" id="f-name" placeholder="Enter frist name" name="f-name"/>
                                        </Col>
                                        <Col lg={6}>
                                            <FormControl type="text" id="l-name" placeholder="Enter last name" name="l-name"/>
                                        </Col>
                                    </Row>
                                </FormGroup>
                                <FormGroup>
                                    <FormControl type="text" id="address" placeholder="Enter address"name="address"/>
                                </FormGroup>
                                <FormGroup >
                                    <FormControl type="email" id="email" placeholder="Enter email" name="email"/>
                                </FormGroup>
                                <FormGroup>
                                    <FormControl type="number" id="phone" placeholder="Enter phone" name="phone"/>
                                </FormGroup>
                                <FormGroup>
                                    <FormControl type="text" id="city" placeholder="Enter city" name="city"/>
                                </FormGroup>
                                <FormGroup className="name-info-user">
                                    <Row>
                                        <Col lg={6}>

                                                <select  id="country">
                                                    <option value="0">Your Country</option>
                                                    <option value="VN">VN</option>
                                                    <option value="Phap">Phap</option>
                                                    <option value="Japaneses">Japaneses</option>
                                                    <option value="Korea">Korea</option>
                                                </select>

                                        </Col>
                                        <Col lg={6}>
                                            <FormControl type="text" id="code" placeholder="Enter Postal Code" name="code"/>
                                        </Col>
                                    </Row>
                                </FormGroup>
                            </Form>
                        </Col>
                        <Col lg={6} md={6} sm={6} xs={12} className="payment-content">
                        <Form >
                            <FormGroup >
                                <FormControl type="email" id="email" placeholder="Your email" name="email"
                                    />
                            </FormGroup>
                            <FormGroup  >
                                <FormControl id="phone" placeholder="Your phone" name="phone"/>
                            </FormGroup>
                            <FormGroup >
                                <textarea className="form-control" id="message" placeholder="Your message" name="message" rows="5"
                                        cols="20"></textarea>
                            </FormGroup>
                            <div className="alert alert-warning message-error" >
                            </div>
                            <Button type="button" className="btn btn-default btn-send-email">
                                SEND
                            </Button>
                        </Form>
                        </Col>
                    </Row>
                </Grid>
            </div>
          );
      }

}

export default ContentPaymentPage;
