import React, { Component } from 'react';
import {Row,Col,Table} from 'react-bootstrap';
import Api from '../Api';
import '../css/contactPage.css';

class InfoOrder extends Component {
    constructor(props) {
        super(props);
        this.state = {
            content: [],
        }
        this.loadOrder = this.loadOrder.bind(this);
    }

    loadOrder(){
        let checkLogin = localStorage.getItem("tokenCustomer");
       
        if(checkLogin!= null){
          let data = {
            token: checkLogin
          }
            Api.get("http://127.0.0.1:8000/checkLogin", data)
            .then((result) => {
                console.log(result)
                if(result.status){
                    let name = result['customer']['name'];
                    let address = result['customer']['address'];
                    let city = result['customer']['city'];
                    let phone = result['customer']['phone'];
                    let email = result['customer']['email'];
                    let country = result['customer']['country'];
                    let check = false;
    
                    if (name == null) {
                        name = '';
                        check = true;
                    }
                    if (city == null) {
                        name = '';
                        check = true;
                    }
                    if (address == null) {
                        address = '';
                        check = true;
                    }
                    if (phone == null) {
                        phone = '';
                        check = true;
                    }

                    window.jQuery('#name').val(name);
                    window.jQuery('#address').val(address);
                    window.jQuery('#email').val(email);
                    window.jQuery('#phone').val(phone);
                    window.jQuery('#city').val(city);
                }
            })
        }else{
            window.location.assign("/");
        }
    }
     
    componentDidMount(){
        this.loadInfoUser();
    }


    render() {
        return (
            <div>
                <Row className="row is-flex account-info-general">
                    <Col lg={12}>
                    <Table id="cart" className="table table-hover table-condensed">
                        <thead>
                        <tr>
                            <th>Order At.</th>
                            <th>No.</th>
                            <th>Name</th>
                            <th>Price</th>
                            <th>Quantity</th>
                            <th className="text-center">Subtotal</th>
                            <th >Ajust</th>
                        </tr>
                        </thead>
                        <tbody id="my-order">
                        <tr>
                            <td colSpan="7">
                                You're not use to order
                                <a href="/home" className="btn btn-warning"><i className="fa fa-angle-left"></i>
                                    Go Shopping Now<span className="glyphicon glyphicon-shopping-cart"></span></a>
                            </td>
                        </tr>
                        </tbody>
                        <tfoot>
                        </tfoot>
                    </Table>
                    </Col>
                </Row>
            </div>
        );
    }
}

export default InfoOrder;
