import React from 'react';
import logo from'./logo.svg';


class Header extends React.Component { 
  constructor (props) {
    super(props)
    // state object
    this.state = { name : this.props.name,id : this.props.id,num : this.props.number}    
  }
  countUp (number) {
    // cập nhật tham số của state object → render method được gọi và vẽ lại
    this.setState({ num : number + 1 })
  }

  countDown (number) {
    // cập nhật tham số của state object → render method được gọi và vẽ lại
    this.setState({ num : number - 1 })
  }
  render() {
  	var num =this.state.num;
  	var name =this.state.name;
  	if(this.state.name =='lalala'){
  		return (


    	<div>
    	<table class="table">
    		<tr>
    			<td>Name</td>
    			<td><input type="text" value="name"/>{this.state.name}</td>
    		</tr>
    		<tr>
    			<td>Number</td>
    			<td><input type="text" value="'{this.state.num}'"/>{this.state.num}</td>
    		</tr>
    		<tr>
    			<td></td>
    			<td><button type="button" onClick={(e)=> this.countUp(this.state.num)} >+</button> <button type="button" onClick={(e)=> this.countDown(this.state.num)} >-</button></td>
    		</tr>
    	</table>
    	<img src="https://img1.kakaku.k-img.com/images/article/pr/18/11_ATH-CKR7TW/img02.jpg"/>

    	</div>
    	);
  	}
    return (


    	<div>

    	<img src="https://img1.kakaku.k-img.com/images/article/pr/18/11_ATH-CKR7TW/img02.jpg"/></div>
    );
  }
}

export default Header;
