import React, { Component } from 'react';
import './App.css';
import {Navbar,FormGroup,Button,FormControl,Nav,NavDropdown,NavItem,MenuItem} from 'react-bootstrap';
import Data from './Data';
import ProductRowSearch from './TableComponent/ProductRowSearch';
class Header extends Component {

  constructor(props) {
    super(props);
    this.state = {
        content: this.buildContent()
    }
  }

  buildContent() {
    let _content = [];
    let cate = Data.cate;
    for(var i in cate) { 
      let href = '/productCateId/' +cate[i].id;
      _content.push(
           <MenuItem key={'2.'+i} href={href}>{cate[i].name}</MenuItem>
      );
    }
    let _cateContent = [];
    _cateContent.push(<NavDropdown key={2} title="Products" id="mydrop">{_content}<MenuItem divider /><MenuItem eventKey={2.5} href="/contact">Contact</MenuItem></NavDropdown>);
    return _cateContent;
  }

 

  componentDidMount() {
    window.jQuery('#search').autocomplete({
    
      source: function (request, response) {
        response(window.jQuery.map(Data.searchAutocomplete(request.term), function (value, key) {
            return {
                label: value.product_name,
                value: value.product_name,
                id: value.id,
                image: value.image,
            }
        }))
      },
      minLength: 1,
      autoFocus: true,
      select: function (e,ui) {
          window.jQuery('#search-id').val(ui.item.id);
          window.jQuery('.search-form').click(function(){
            window.location.assign('/product/'+ window.jQuery('#search-id').val());
        });
      },
    }).data("ui-autocomplete")._renderItem = function(ul, item) {
        let k = ul.parent();
        console.log(k);
        
        ul.css('width','144px');
        window.jQuery('#ui-id-1').css('width','100px');
        ul.css('background-color','black');
        console.log( ul.parent().css('width'));
        console.log(  window.jQuery('#ui-id-1').css('width'));
        let html = '';
        html += '<h5 style="color:white;">'+item.value+'</h5>';
        return  window.jQuery("<li style='background-color: black;list-style-type: none;width:10%'>").data("ui-autocomplete-item", item).append(html).appendTo(ul);
    };


  
  } 


  render() {
    return (
      <div>
        <Navbar>
          <Navbar.Header>
            <Navbar.Brand>
              <a  href="/">Guitar 4U</a>
            </Navbar.Brand>
            <Navbar.Toggle />
          </Navbar.Header>
          <Navbar.Collapse>
            <Nav>
              <NavItem eventKey={1} href="/">
                Home
              </NavItem>
              {this.state.content}
            </Nav>
            
           
            <Nav pullRight>
              <NavItem href="/login">
                Login
              </NavItem>
              <NavItem href="/signUpPage">
                Sign Up
              </NavItem>
            </Nav>
            <Navbar.Form pullRight >
                <FormGroup >
                  <FormControl type="text" id="search" placeholder="Search" />
                  <FormControl type="hidden" id="search-id" name="id"/>
                </FormGroup>
                <Button type="button" className="search-form" >Search</Button>
              </Navbar.Form>
          </Navbar.Collapse>
        </Navbar>
      </div>
    );
  }
}

export default Header;
