import React, { Component } from 'react';
import './App.css';
import {Navbar,FormGroup,Button,FormControl,Nav,NavDropdown,NavItem,MenuItem} from 'react-bootstrap';
import Data from './Data';

class Header extends Component {

  constructor(props) {
    super(props);
    this.state = {
        content: this.buildContent()
    }
    this.searchAutocomplete = this.searchAutocomplete.bind(this);
  }

  buildContent() {
    let _content = [];
    let cate = Data.cate;
    for(var i in cate) { 
      let href = '/productCateId/' +cate[i].id;
      _content.push(
           <MenuItem key={'2.'+i} href={href}>{cate[i].name}</MenuItem>
      );
    }
    let _cateContent = [];
    _cateContent.push(<NavDropdown key={2} title="Products" id="mydrop">{_content}<MenuItem divider /><MenuItem eventKey={2.5} href="/contact">Contact</MenuItem></NavDropdown>);
    return _cateContent;
  }

  searchAutocomplete(e){
    
    e.preventDefault();
    let _productListSearch = [];
    for(let data in Data.data){
      if(data.product_name.search('a')){
        _productListSearch.push(data);
      }
    }
    return _productListSearch;
  }

  componentDidMount() {
    window.jQuery('#search').autocomplete({
    
      source: function (request, response) {
        console.log();
        response(window.jQuery.map(Data.data, function (value, key) {
            return {
                label: value.product_name,
                value: value.product_name,
                id: value.id,
                image: value.image,
            }
        }))
      },
      minLength: 1,
      autoFocus: true,
      select: function ( ui) {
          window.jQuery('#search-id').val(ui.item.id);
      },
    }).data("ui-autocomplete")._renderItem = function(ul, item) {
        let html = '';
        html+= '                        <div class="row">\n' +
            '                            <div class="col-lg-6 col-sm-6 col-xs-6 col-md-6 item-search" ><div ">'+item.label+'</div></div>\n' +
            '                            <div class="col-lg-6 col-sm-6 col-xs-6 col-md-6 item-search" ><img alt="product"style="width: 30px;" src='+item.image+'"></td>\n' +
            '                        </div>';
        return  window.jQuery("<li style='list-style-type: none'>").data("ui-autocomplete-item", item).append(html).appendTo(ul);
    };
  } 


  render() {
    return (
      <div>
        <Navbar>
          <Navbar.Header>
            <Navbar.Brand>
              <a  href="/">Guitar 4U</a>
            </Navbar.Brand>
            <Navbar.Toggle />
          </Navbar.Header>
          <Navbar.Collapse>
            <Nav>
              <NavItem eventKey={1} href="/">
                Home
              </NavItem>
              {this.state.content}
            </Nav>
            
           
            <Nav pullRight>
              <NavItem href="/login">
                Login
              </NavItem>
              <NavItem href="/signUpPage">
                Sign Up
              </NavItem>
            </Nav>
            <Navbar.Form pullRight>
                <FormGroup>
                  <FormControl type="text" onKeyUp={this.searchAutocomplete} id="search" placeholder="Search" />
                  <FormControl type="hidden" id="search-id" name="id"/>
                </FormGroup>{' '}
                <Button type="submit">Submit</Button>
              </Navbar.Form>
          </Navbar.Collapse>
        </Navbar>
      </div>
    );
  }
}

export default Header;
