import React, { Component } from 'react';
import './App.css';
import {Navbar,FormGroup,Button,FormControl,Nav,NavDropdown,NavItem,MenuItem} from 'react-bootstrap';
import Data from './Data';

class Header extends Component {

  constructor(props) {
    super(props);
    this.state = {
        content: []
    }
  }
  buildContent() {
    let _content = [];
    let cate = Data.cate;
    for(var i in cate) { 
      let href = '/productCateId/' +cate[i].id;
      _content.push(
           <MenuItem eventKey={i} href={href}>{cate[i].name}</MenuItem>
      );
    }
    let _cateContent = [];
    _cateContent.push(<NavDropdown eventKey={3} title="Products" id="mydrop">{_content}<MenuItem divider /><MenuItem eventKey={2.5}>Contact</MenuItem></NavDropdown>);
    return _cateContent;
  }

  componentDidMount() {
    this.setState({
        content: this.buildContent()
    });
  } 


  render() {
    return (
      <div>
        <Navbar>
          <Navbar.Header>
            <Navbar.Brand>
              <a  href="/">Guitar 4U</a>
            </Navbar.Brand>
            <Navbar.Toggle />
          </Navbar.Header>
          <Navbar.Collapse>
            <Nav>
              <NavItem eventKey={1} href="/">
                Home
              </NavItem>
              {this.state.content}
            </Nav>
            
           
            <Nav pullRight>
              <NavItem href="#">
                Login
              </NavItem>
              <NavItem href="#">
                Sign Up
              </NavItem>
            </Nav>
            <Navbar.Form pullRight>
                <FormGroup>
                  <FormControl type="text" placeholder="Search" />
                </FormGroup>{' '}
                <Button type="submit">Submit</Button>
              </Navbar.Form>
          </Navbar.Collapse>
        </Navbar>
      </div>
    );
  }
}

export default Header;
